-- title:  TICWars
-- author: gist974@gmail.com
-- desc:   Advance Wars Demake
-- script: lua
-- input:  mouse

x=0
y=0
p=0
t=0

function TIC()
 cls()

 --get mouse info
 x,y,p=mouse()
 
 --draw stuff
 t = t+1

 map(0,0) 
 spr(256+t%60//20*2,20,80,5,1,1,0,2,2)
 spr(264+t%60//20*2,150,35,5,1,0,0,2,2)
 
  --if pressed
 if p then spr(288+t%60//10*2,x-16,y-16,5,1,0,0,2,2) end
 
 --show coordinates
 c=string.format('(%03i,%03i)',x,y)
 print(c,0,0,15,1)

end

