-- title:  Axayacalt Remake
-- author: VERHILLE Arnaud - gist974@gmail.com
-- desc: Axayacalt's Tomb 2 pour TIC-80
-- graphics: 24x24 tiles & sprites by Petitjean & Shurder
-- script: lua

t=0

game = {
 state = -1 -- initialization needed
}

camera = {
	x=0,y=0
}

player = {
 x,y,aim
}

monster = {
	x,y,rot
}

function TIC()
 t=t+2
 if game.state == -2 then flyByMode() end
 if game.state == -1 then initAttractMode() end
 if game.state == 0 then attractMode() end
 if game.state == 1 then initGame() end	 
end

function initAttractMode()
 player.x = 7*24
 player.y = 3*24
 player.aim = -1
 monster.x = 1*24
 monster.y = 3*24
 monster.rot = 0
 game.state = 0
end

function initGame()

end

function flyByMode()
	cls(0)
	if btn(0) then camera.y = camera.y -1 end
	if btn(1) then camera.y = camera.y +1 end
	if camera.y < 0 then camera.y =0 end
	if camera.y > 130 then camera.y =130 end
	if btn(2) then camera.x = camera.x -1 end
	if btn(3) then camera.x = camera.x +1 end
	if camera.x < 0 then camera.x =0 end
	if camera.x > 230 then camera.x = 230 end
	drawMap(camera.x,camera.y)
end

function attractMode()
	cls(0)
	updatePlayer()
	updateMonster()
	drawMap(230,130)
	drawPlayer()
	drawMonster()
	drawText()
end

function updatePlayer()
 if btn(0) then 
  player.aim = -1 end
 if btn(1) then 
  player.aim = -1 end
 if btn(2) then 
  player.aim = -1 end
 if btn(3) then 
  player.aim = -1 end

 if btn(4) then player.aim = 270 end
 if btn(5) then player.aim = 180 end
 if btn(6) then player.aim = 0 end
 if btn(7) then player.aim = 90 end
end

function updateMonster()
 if monster.x < 1*24 then
	 monster.rot = 0
	end
	if monster.x > 5*24 then
	 monster.rot = 1
	end
	if monster.rot == 0 then
	 monster.x = monster.x + 1
	end
	if monster.rot == 1 then
		monster.x = monster.x - 1 
	end
end	

function drawMap(x,y) -- draw 64x64 Sprite Map
	for i=0,9 do
	 for j=0,5 do
		-- Get the mini-sprite value from TIC-80 map
		val=peek(0x08000+(i+x)+(j+y)*240) 
		drawMapSprite(val,i,j)
	 end
 end
end

function drawMapSprite(val,i,j)
 -- Wall 1 
 if val == 1 then spr(65,i*24,j*24,-1,1,0,0,3,3) end
 -- Secret Wall 1
 if val == 2 then
	 spr(65,i*24,j*24,-1,1,0,0,3,3)
	 spr(80,i*24+8,j*24+8,-1,1,0,0,1,1)
 end
 -- Wall 2 
 if val == 3 then spr(68,i*24,j*24,-1,1,0,0,3,3) end
 -- Stairs left
 if val == 4 then 
  spr(71,i*24,j*24,-1,1,0,0,2,3) 
  spr(72,i*24+16,j*24,-1,1,0,0,1,3) 
 end
 -- Stairs center
 if val == 5 then
	 spr(72,i*24,j*24,-1,1,0,0,1,3)
	 spr(72,i*24+8,j*24,-1,1,0,0,1,3)
	 spr(72,i*24+16,j*24,-1,1,0,0,1,3) 
 end
 -- Stairs right
 if val == 6 then 
  spr(72,i*24,j*24,-1,1,0,0,1,3) 
  spr(72,i*24+8,j*24,-1,1,0,0,2,3) 
 end
 -- Pikes
 if val == 7 then spr(160,i*24,j*24,-1,1,0,0,3,3) end
 -- Little Skull
 if val == 8 then spr(204,i*24,j*24,-1,1,0,0,3,2) end
 -- Big Skull
 if val == 9 then spr(236,i*24,j*24,-1,1,0,0,3,2) end
 -- Aztec Statue
 if val == 10 then spr(163,i*24,j*24,-1,1,0,0,3,3) end
 -- Weird Statue
 if val == 11 then spr(118,i*24,j*24,-1,1,0,0,3,3) end
 -- Pedestal
 if val == 12 then spr(166,i*24,j*24,-1,1,0,0,3,3) end
 -- Book
 if val == 13 then spr(13,i*24+8,j*24+16,-1,1,0,0,1,1) end
 -- Water
 if val == 14 then spr(112,i*24,j*24,-1,1,0,0,3,3) end
 -- Pillar
 if val == 15 then spr(208,i*24,j*24,-1,1,0,0,3,3) end
end

function drawPlayer()
 if player.aim == -1 then spr(256,player.x,player.y,0,1,0,0,3,3) end
 if player.aim == 0 then spr(304,player.x,player.y,0,1,0,0,3,3) end
 if player.aim == 90 then spr(310,player.x,player.y,0,1,0,0,3,3) end
 if player.aim == 180 then spr(307,player.x,player.y,0,1,0,0,3,3) end
 if player.aim == 270 then spr(313,player.x,player.y,0,1,0,0,3,3) end
end

function drawMonster()
	spr(352+t%60//30*3,monster.x,monster.y,0,1,monster.rot,0,3,3)
end

function drawText()
	print("Axayacalt's",65,1*24+6,10,2,2)
	print("Tomb 2",90,2*24,10,2,2)
	print("A TIC-80 game by gist974",50,5*24+2,1,1,1)
	print("Graphics: Petitjean & Shurder",35,5*24+10,1,1,1)
end
