-- title:  Axayacalt Remake
-- author: VERHILLE Arnaud
-- desc:   Le tombeau d'Axayacalt pour TIC-80
-- script: lua

t=0

player = {
 x = 168,y =81,
 aim = -1
}

monster = {
	x = 24,y = 81,
	dir = 0
}

function TIC()

	t=t+2
	
	cls(13)
	
	map(0,0)
	print("Le Tombeau",70,40,10,2,2)
	print("d'Axayacalt",65,60,10,2,2)
	
	updatePlayer()
	drawPlayer()
	drawMonster()
		
end

function updatePlayer()
 if btn(0) then 
  player.aim = -1 end
 if btn(1) then 
  player.aim = -1 end
 if btn(2) then 
  player.aim = -1 end
 if btn(3) then 
  player.aim = -1 end

 if btn(4) then player.aim = 270 end
 if btn(5) then player.aim = 180 end
 if btn(6) then player.aim = 0 end
 if btn(7) then player.aim = 90 end
end

function drawPlayer()
 if player.aim == -1 then spr(256,player.x,player.y,0,1,0,0,3,3) end
 if player.aim == 0 then spr(304,player.x,player.y,0,1,0,0,3,3) end
 if player.aim == 90 then spr(310,player.x,player.y,0,1,0,0,3,3) end
 if player.aim == 180 then spr(307,player.x,player.y,0,1,0,0,3,3) end
 if player.aim == 270 then spr(313,player.x,player.y,0,1,0,0,3,3) end
end

function drawMonster()
 if monster.x < 25 then
	 monster.dir = 0
	end
	if monster.x > 120 then
	 monster.dir = 1
	end
	if monster.dir == 0 then
	 monster.x = monster.x + 1
	end
	if monster.dir == 1 then
		monster.x = monster.x - 1 
	end
	
	spr(352+t%60//30*3,monster.x,monster.y,0,1,monster.dir,0,3,3)
end
